import '../styles/style.css'

function PremierComposant() {
    const moisCourant = new Date().getMonth();
    const isSpring = moisCourant >= 2 && moisCourant <= 5

    if(isSpring)
    {
        return <h1 className='titre-principal'>Nous sommes bien au printemps !</h1>
    }
    else
    {
        return <h1 className='titre-principal'>Nous ne sommes pas au printemps !</h1>
    }
    
}

export default PremierComposant
