import '../styles/style.css'

function PremierComposant(props) {
    const moisCourant = new Date().getMonth();
    const isSpring = moisCourant >= 2 && moisCourant <= 5;

    const onFire = props.myObject.onFire;

    const range = [1,2,3];

    if(isSpring)
    {
        return (
        <div>
        <h1 className='isSpring'>Nous sommes bien au printemps ! {props.myObject.prenom} ðŸ”¥ </h1>
        
            {range.map((rangeElem) =>
                onFire >= rangeElem ? <span key={rangeElem.toString()}> ðŸ”¥ </span> : null
            )}
        </div>
        
        )
    }
    else
    {
        return <h1 className='isNotSpring'>Nous ne sommes pas au printemps ! {props.name}</h1>
    }
    
}

export default PremierComposant
